sub process {
	$extra = shift;
	$name = shift;
	$filename = shift;
	$lexicalPattern = shift;

	undef %allowed;
	foreach $s (@s1000) {
		$allowed{$s} = 1;
	}
	foreach $s (@rest) {
		$allowed{$s} = 1;
	}
	@x = split(',',$extra);
	print STDERR "$name has ".($#x+1)." taxa\n";
	foreach $s (@x) {
		$allowed{$s} = 1;
	}


	$text = '';
	open(FIN, '/tmp/edge-completeP.xml');
	while ($s = <FIN>) {
		if ($s =~ /id="lexical-priors"/ && $lexicalPattern eq 'xxx') {
			# eat up distribution;
			$d = 1;
			while ($d > 0) {
				$s = <FIN>;
				if ($s =~ /<\/distribution>/) {
					$d--;
				} elsif ($s =~ /<distribution/) {
					$d++;
				}
			}
			$s = '';
		} elsif ($s =~ /<log idref="lexical-priors"\/>/ && $lexicalPattern eq 'xxx') {
			$s = '';		
		} elsif (($s =~ /id="lexical_/) && ($s !~ /id="$lexicalPattern/)) {
			# remove distribution for which the taxon set became empty after removing taxa
			while (($s = <FIN>) && ($s !~ /<\/distribution>/)) {
			
			}
			$s = '';
		} elsif ($s =~ /<taxon id="(........)"\/>/) {
			$t = $1;
			if ($allowed{$t} != 1) {
				$s = '';
			}
		} elsif ($s =~ /<taxon idref=["'](........)["']\/>/) {
			$t = $1;
			if ($allowed{$t} != 1) {
				$s = '';
			}
		} elsif ($s =~ /^([a-z][a-z][a-z][a-z][0-9][0-9][0-9][0-9]) =/) {
			$t = $1;
			if ($allowed{$t} != 1) {
				$s = '';
			}
		} elsif ($s =~ /newick/) {
			processNewick();
		} 

		$text .= $s;
	}

	$text =~ s/<distribution id='[^>]+>\s*\n\s*<taxonset[^>]+>\s*\n\s*<\/taxonset>\s*\n\s*<[^>]+>\s*\n\s*<\/distribution>//g;
	$text =~ s/<distribution id='[^>]+>\s*\n\s*<taxonset[^>]+>\s*\n\s*<taxon idref=[^>]+>\s*\n\s*<\/taxonset>\s*\n\s*<[^>]+>\s*\n\s*<\/distribution>//g;
	
	$text =~ s/fileName="EDGE6635/fileName="EDGE6635-$name/g;
#	$text =~ s/<constraints idref="extra-atlantic-congo-init-constraints"\/>/<!--constraints idref="extra-atlantic-congo-init-constraints"\/-->/;
	close FIN;
	
	
	open(FOUT,">/tmp/x");
	print FOUT $text;
	close FOUT;
	
	open(FOUT,">$filename");
	open (FIN,"/tmp/x");
	undef %tabuprior;
	$kml = 0;
	while ($s = <FIN>) {
	 if ($s =~ /<distribution.*PrunedTree">/) {
			# make sure pruned calibrations have sufficient taxa left
			$s2 = <FIN>;
			$s3 = <FIN>;
			$s4 = <FIN>;
			if ($s3 =~ /<\/taxonset>/) {
				$s=<FIN>;
				$s=<FIN>;
				$s=<FIN>;
				$s=<FIN>;
 				$s = ''; 
			} elsif ($s4 =~ /<\/taxonset>/) {
				$s=<FIN>;
				$s=<FIN>;
				$s=<FIN>;
				$s=<FIN>;
				$s=<FIN>;
 				$s = ''; 
			} else {
				print FOUT $s;
				print FOUT $s2;
				print FOUT $s3;
				print FOUT $s4;
				$s = '';
			}
		} elsif ($s =~ /beast.math.distributions.MRCAPrior\"/) {
			$s =~ /id="([^"]*).prior"/;
			$id = $1;
 			# make sure calibrations have sufficient taxa left
			$s2 = <FIN>;
			$s3 = <FIN>;
			$s4 = <FIN>;
			if ($s3 =~ /<\/taxonset>/) {
				# no taxa
				while ($s !~ /<\/distribution>/) {				
					$s=<FIN>;
				}
 				$s = ''; 
				$tabuprior{$id} = 1;
			} elsif ($s4 =~ /<\/taxonset>/) {
				# 1 taxon
				if ($s !~ /useOriginate/) {
					while ($s !~ /<\/distribution>/) {				
						$s=<FIN>;
					}
	 				$s = ''; 
				$tabuprior{$id} = 1;
 				} else {
					print FOUT $s;
					print FOUT $s2;
					print FOUT $s3;
					print FOUT $s4;
					$s = '';
 				}
			} else {
				# 2 taxa or more
				print FOUT $s;
				print FOUT $s2;
				print FOUT $s3;
				print FOUT $s4;
				$s = '';
			}
		} if ($s =~ /idref="(.*).prior"/) {
			$id = $1;
			if ($tabuprior{$id} == 1) {
				$s = '';
			} 			
		}
		$s =~ s/ dimension="[^"]+"//;
		if ($s =~ /spec="sphericalGeo.region.KMLRegion"/) {
			if ($kml == 0) {
				$kml++;
				$s =~ s/spec="sphericalGeo.region.KMLRegion"/spec="sphericalGeo.region.KMLRegion" kml=".\/kml\/Africa.kml"/;
			} else {
				$s =~ s/spec="sphericalGeo.region.KMLRegion"/spec="sphericalGeo.region.KMLRegion" kml=".\/kml\/SouthAsia.kml"/;
			}
		}
		$s =~ s/<distr id="Normal([0-9]+)" spec="beast.math.distributions.Normal">/<distr id="Normal$1" spec="beast.math.distributions.AlmostNormal">/;
		$s =~ s/<kml id="File.*" spec="java.io.File"\/>//;
		print FOUT $s;
	}
	close FIN;
	close FOUT;
