sub processNewick {
	$s =~ /newick=['"]([^"']*)['"]/;
	$org = $1;
	$newick = '';
	for ($i = 0; $i < length($org); $i++) {
		$c = substr($org,$i,1);
		if ($c eq '(' || $c eq ')' || $c eq ',') {
			$newick .= $c;
		} else {
			$t = substr($org, $i, 8);
			if ($allowed{$t} == 1) {
				$newick .= $t;
			}
			$i += 7;
		}
	}
	do {
		$len = length($newick);
		normalize($newick);
	} while ($len != length($newick));
	#replaceSingeChildNodes();
	#print STDERR "Post: $newick\n";
	$s =~ s/newick=['"][^"']*['"]/newick="$newick"/;
}

sub normalize {
	$newick =~ s/\(,/\(/g;
	$newick =~ s/,\)/)/g;
	$newick =~ s/,,/,/g;
	$newick =~ s/\(\)//g;	
	$newick =~ s/\(([a-z][a-z][a-z][a-z][0-9][0-9][0-9][0-9])\)/$1/g;
	$newick =~ s/\(\(([^\(\)]+)\)\)/($1)/g;

#special cases
#	$newick =~ s/\(\(\(msk,daw\),cps,tsg\)\)/((msk,daw),cps,tsg)/;
#	$newick =~ s/\(\(\(bum,ewo\),mmu,swb,her,nnq,tsc,zmw,kzy,sna,zmn,wbh,nda,bcp,boh,bvb,lag,lea,nim,poy,soo,soz\)\)/((bum,ewo),mmu,swb,her,nnq,tsc,zmw,kzy,sna,zmn,wbh,nda,bcp,boh,bvb,lag,lea,nim,poy,soo,soz)/;
#	$newick =~ s/\(\(\(\(sme,mdf\),mrj\),\(kca,mns\),koi,hun\)\)/(((sme,mdf),mrj),(kca,mns),koi,hun)/;
#	$newick =~ s/\(\(\(tnf,wrd\),aiq\)\)/((tnf,wrd),aiq)/;
}


sub replaceSingeChildNodes {
	$org = $newick;
	$newick = '';
	for ($i = 0; $i < length($org); $i++) {
		$c = substr($org,$i,2);
		if ($c eq '((') {
			$j = matchingBrackets($i);
			if ($j > 0) {
				$org = substr($org,0,$j).substr($org,$j+1);
			} else {
				$newick .= substr($org, $i, 1);
			}
		} else {
			$newick .= substr($org, $i, 1);
		}
	}	
}

sub matchingBrackets {
	$k = $i;
	$n = 0;
	while ($k == $i || ($k < length($org) && $n > 0)) {
		$c = substr($org, $k, 1);
		if ($c eq '(') {
			$n++;
		} elsif ($c eq ')') {
			$n--;
			if ($n == 0 && substr($org, $k-1, 1) eq ')') {
				print STDERR "Match at $k\n";
				return $k;
			}
		}
		$k++;
	}
	return -1;
}